import * as React from 'react';
import { SegmentMenu } from './segment-menu';
import {default as TaskItems } from '../data/mock-data';
import { ISegmentMenuItems } from '../types/segment-types';
import { MetaDataDetails } from './metadata-component';

interface AppProps{
  menuItems: any;
 }
interface AppState{ containerExpanded: boolean; }
 
export class TaskCards extends React.Component<AppProps, AppState> {
    constructor(props: AppProps) {
        super(props);
        this.state = { containerExpanded : true};
    }

    slideTaskDetails = (event) => {
      this.setState({containerExpanded: (this.state.containerExpanded) ? false : true});
    }

    prepareSegmentMenuItems(){
      return [
        {
          key: 'process-viewer',
          name: 'process-viewer',
          icon: 'process-icon',
          onClick: (e) => {
            alert('click');
          }
        },
        {
          key: 'open-form',
          name: 'open-form',
          icon: 'openform-icon',
          onClick: (e) => {
            alert('click');
          }
        },
        {
          key:'change-flow',
          name: 'Change Flow',
          icon: 'changeflow-icon',
          onclick: (e) => {
            alert('change flow');
          }
        }
      ];
    }

    prepareFarItems(){
      return [{
          key:'release-to-queue',
          name: 'Release to Queue',
          icon: 'release-to-queue-icon',
          onclick: (e) => {
            alert('change flow');
          }
        },
        {
          key:'rework',
          name: 'Rework',
          icon: 'rework-icon',
          onclick: (e) => {
            alert('change flow');
          }
        },
        {
          key:'create-worked-item',
          name: 'Create Linked Worked Item',
          icon: 'create-worked-item-icon',
          onclick: (e) => {
            alert('change flow');
          }
        },
        {
          key:'change-priority',
          name: 'Change Priority',
          icon: 'change-priority-icon',
          onclick: (e) => {
            alert('change flow');
          }
        },
        {
          key:'cancel-process',
          name: 'Cancel Process',
          icon: 'cancel-process-icon',
          onclick: (e) => {
            alert('change flow');
          }
        },
        {
          key:'view-description',
          name: 'View Description',
          icon: 'view-description-icon',
          onclick: (e) => {
            alert('change flow');
          }
        }];
    }
 
    render() {
        return (
            <div className="task-card" style={{ height: this.state.containerExpanded ? '200px' : '400px'}}>
                <SegmentMenu menuItems={this.prepareSegmentMenuItems()} farItems={this.prepareFarItems()} />

                <div className="processname">
                    {TaskItems.HomePageData[0].applicationName}
                </div>
                <div className="appname">
                    {TaskItems.HomePageData[0].processName}
                </div>

                <div className="priority">
                    <span>Priority: </span> {TaskItems.HomePageData[0].priority}
                </div>

                <div className="time-details">
                    <div className="item-details">
                        <span><img src="data:image/png;base64," /></span>
                        <span>{TaskItems.HomePageData[0].timeDetails.processtarted}</span>
                    </div>
                    <div className="item-details right-align">
                        <span><img src="data:image/png;base64," /></span>
                        <span>{TaskItems.HomePageData[0].timeDetails.dueDays}</span>
                    </div>
                </div>

                <MetaDataDetails metaDataItems={TaskItems.HomePageData[0].metaDataDetails} />

               
                <div className={`slideButton ${this.state.containerExpanded ? 'slideout' : 'slidein'}`} onClick={this.slideTaskDetails}>
                </div>
            </div>
        );
    }
}